CREATE TABLE `payment`(`payment_id` SMALLINT UNSIGNED NOT NULL, `customer_id` SMALLINT UNSIGNED NOT NULL, `staff_id` SMALLINT UNSIGNED NOT NULL, `rental_id` INT NULL, `amount` DECIMAL NOT NULL, `payment_date` DATETIME NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `customer`(`customer_id` SMALLINT UNSIGNED NOT NULL, `store_id` SMALLINT UNSIGNED NOT NULL, `first_name` VARCHAR(45) NOT NULL, `last_name` VARCHAR(45) NOT NULL, `email` VARCHAR(50) NULL, `address_id` SMALLINT UNSIGNED NOT NULL, `active` BIT NOT NULL, `create_date` DATETIME NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `category`(`category_id` SMALLINT UNSIGNED NOT NULL, `name` VARCHAR(25) NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `rental`(`rental_id` INT NOT NULL, `rental_date` DATETIME NOT NULL, `inventory_id` MEDIUMINT UNSIGNED NOT NULL, `customer_id` SMALLINT UNSIGNED NOT NULL, `return_date` DATETIME NULL, `staff_id` SMALLINT UNSIGNED NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `actor`(`actor_id` SMALLINT UNSIGNED NOT NULL, `first_name` VARCHAR(45) NOT NULL, `last_name` VARCHAR(45) NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `store`(`store_id` SMALLINT UNSIGNED NOT NULL, `manager_staff_id` SMALLINT UNSIGNED NOT NULL, `address_id` SMALLINT UNSIGNED NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `inventory`(`inventory_id` MEDIUMINT UNSIGNED NOT NULL, `film_id` SMALLINT UNSIGNED NOT NULL, `store_id` SMALLINT UNSIGNED NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `film_actor`(`actor_id` SMALLINT UNSIGNED NOT NULL, `film_id` SMALLINT UNSIGNED NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `address`(`address_id` SMALLINT UNSIGNED NOT NULL, `address` VARCHAR(50) NOT NULL, `address2` VARCHAR(50) NULL, `district` VARCHAR(20) NOT NULL, `city_id` SMALLINT UNSIGNED NOT NULL, `postal_code` VARCHAR(10) NULL, `phone` VARCHAR(20) NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `staff`(`staff_id` SMALLINT UNSIGNED NOT NULL, `first_name` VARCHAR(45) NOT NULL, `last_name` VARCHAR(45) NOT NULL, `address_id` SMALLINT UNSIGNED NOT NULL, `picture` BLOB NULL, `email` VARCHAR(50) NULL, `store_id` SMALLINT UNSIGNED NOT NULL, `active` BIT NOT NULL, `username` VARCHAR(16) NOT NULL, `password` VARCHAR(40) NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `film`(`film_id` SMALLINT UNSIGNED NOT NULL, `title` VARCHAR(255) NOT NULL, `description` TEXT NULL, `release_year` YEAR NULL, `language_id` SMALLINT UNSIGNED NOT NULL, `original_language_id` SMALLINT UNSIGNED NULL, `rental_duration` SMALLINT UNSIGNED NOT NULL, `rental_rate` DECIMAL NOT NULL, `length` SMALLINT UNSIGNED NULL, `replacement_cost` DECIMAL NOT NULL, `rating` ENUM NULL, `special_features` SET NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `film_category`(`film_id` SMALLINT UNSIGNED NOT NULL, `category_id` SMALLINT UNSIGNED NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `film_text`(`film_id` SMALLINT NOT NULL, `title` VARCHAR(255) NOT NULL, `description` TEXT NULL);CREATE TABLE `city`(`city_id` SMALLINT UNSIGNED NOT NULL, `city` VARCHAR(50) NOT NULL, `country_id` SMALLINT UNSIGNED NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `country`(`country_id` SMALLINT UNSIGNED NOT NULL, `country` VARCHAR(50) NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `language`(`language_id` SMALLINT UNSIGNED NOT NULL, `name` VARCHAR(20) NOT NULL, `last_update` TIMESTAMP NOT NULL);