CREATE SCHEMA IF NOT EXISTS "public";CREATE TABLE "public"."category"("category_id" serial NOT NULL , "name" varchar(25) NOT NULL , "last_update" timestamp NOT NULL );CREATE TABLE "public"."actor"("actor_id" serial NOT NULL , "first_name" varchar(45) NOT NULL , "last_name" varchar(45) NOT NULL , "last_update" timestamp NOT NULL );CREATE TABLE "public"."country"("country_id" serial NOT NULL , "country" varchar(50) NOT NULL , "last_update" timestamp NOT NULL );CREATE TABLE "public"."city"("city_id" serial NOT NULL , "city" varchar(50) NOT NULL , "country_id" int2 NOT NULL , "last_update" timestamp NOT NULL );CREATE TABLE "public"."address"("address_id" serial NOT NULL , "address" varchar(50) NOT NULL , "address2" varchar(50), "district" varchar(20) NOT NULL , "city_id" int2 NOT NULL , "postal_code" varchar(10), "phone" varchar(20) NOT NULL , "last_update" timestamp NOT NULL );