CREATE SCHEMA IF NOT EXISTS "public";CREATE TABLE "public"."categories"("category" serial NOT NULL , "categoryname" varchar(50) NOT NULL );CREATE TABLE "public"."inventory"("prod_id" int4 NOT NULL , "quan_in_stock" int4 NOT NULL , "sales" int4 NOT NULL );CREATE TABLE "public"."orderlines"("orderlineid" int4 NOT NULL , "orderid" int4 NOT NULL , "prod_id" int4 NOT NULL , "quantity" int2 NOT NULL , "orderdate" date NOT NULL );CREATE TABLE "public"."products"("prod_id" serial NOT NULL , "category" int4 NOT NULL , "title" varchar(50) NOT NULL , "actor" varchar(50) NOT NULL , "price" numeric NOT NULL , "special" int2, "common_prod_id" int4 NOT NULL );CREATE TABLE "public"."orders"("orderid" serial NOT NULL , "orderdate" date NOT NULL , "customerid" int4, "netamount" numeric NOT NULL , "tax" numeric NOT NULL , "totalamount" numeric NOT NULL );CREATE TABLE "public"."cust_hist"("customerid" int4 NOT NULL , "orderid" int4 NOT NULL , "prod_id" int4 NOT NULL );CREATE TABLE "public"."customers"("customerid" serial NOT NULL , "firstname" varchar(50) NOT NULL , "lastname" varchar(50) NOT NULL , "address1" varchar(50) NOT NULL , "address2" varchar(50), "city" varchar(50) NOT NULL , "state" varchar(50), "zip" int4, "country" varchar(50) NOT NULL , "region" int2 NOT NULL , "email" varchar(50), "phone" varchar(50), "creditcardtype" int4 NOT NULL , "creditcard" varchar(50) NOT NULL , "creditcardexpiration" varchar(50) NOT NULL , "username" varchar(50) NOT NULL , "password" varchar(50) NOT NULL , "age" int2, "income" int4, "gender" varchar(1));